import React from "react";
import Slider from "react-slick";
import "./CardSlider.css"; // Crie este arquivo para o CSS

// Ícones ou nomes das tecnologias para os cards
const techStack = [
    "React",
    "JavaScript",
    "HTML",
    "CSS",
    "Git",
    "Node.js"
];


const CardSlider = () => {
    const settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 500,
        rows: 2,
        slidesPerRow: 2,
        autoplay: true,
        autoplaySpeed: 2000,
        arrows: false,
        pauseOnHover: true
      };
    

    return (
        <div className="slider-container">
            <div className="fade-left"></div>
            <Slider {...settings}>
            <div className="card">
                <div><h3>React</h3></div>
                <div><h3>JavaScript</h3></div>
                <div><h3>HTML</h3></div>
                <div><h3>CSS</h3></div>
                <div><h3>Git</h3></div>
                <div><h3>Node.js</h3></div>
                </div>
            </Slider>

            <div className="fade-right"></div>
        </div>
    );
};

export default CardSlider;
