import React, { useEffect, useRef, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const cardWidth = 200; // Largura de cada card
    const totalCards = cards.length; // Número total de cards
    const [offset, setOffset] = useState(0);
    const sliderRef = useRef(null); // Referência para o slider

    useEffect(() => {
        const interval = setInterval(() => {
            setOffset((prevOffset) => {
                const nextOffset = prevOffset + cardWidth; // Mova 200px para a esquerda
                // Reinicie o offset se passar do total de cards
                if (nextOffset >= cardWidth * totalCards) {
                    // Quando o último card sai, redefinimos para zero
                    return 0; 
                }
                return nextOffset;
            });
        }, 2000); // Tempo do intervalo de transição

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [totalCards, cardWidth]);

    // Ajusta a animação quando o offset é resetado
    useEffect(() => {
        if (offset === 0) {
            sliderRef.current.style.transition = "none"; // Remove transição para instantâneo
            setTimeout(() => {
                sliderRef.current.style.transition = "transform 0.5s ease-in-out"; // Adiciona de volta a transição
            }, 50); // Tempo curto para permitir a mudança de transição
        }
    }, [offset]);

    return (
        <div className="slider-container">
            <div
                className="slider"
                ref={sliderRef} // Aplica a referência aqui
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out", // Suaviza a transição
                    width: `${cardWidth * totalCards}px`, // Largura total dos cards
                }}
            >
                {/* Renderizando os cards */}
                {cards.map((card, index) => (
                    <div className="slide" key={index}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
