import React from "react";
import Slider from "react-slick";
import "./CardSlider.css"; // Crie este arquivo para o CSS

// Ícones ou nomes das tecnologias para os cards
const techStack = [
    "React",
    "JavaScript",
    "HTML",
    "CSS",
    "Git",
    "Node.js"
];

const CardSlider = () => {
    const settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 2000,
        rows: 1, // Corrigido para exibir uma linha de cards
        autoplay: false,
        autoplaySpeed: 2000,
        arrows: false,
        pauseOnHover: true,
        cssEase: "linear"
    };

    return (
        <div className="slider-container">
            <div className="fade-left"></div>
            <Slider {...settings}>
                {techStack.map((tech, index) => (
                    <div className="card" key={index}>
                        <h3>{tech}</h3>
                    </div>
                ))}
            </Slider>
            <div className="fade-right"></div>
        </div>
    );
};

export default CardSlider;
