import React from "react";
import Slider from "react-slick";
import "./CardSlider.css"; // Certifique-se de que este arquivo existe para os estilos

const CardSlider = () => {
    const settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 500,
        rows: 2,
        slidesPerRow: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        arrows: false,
        pauseOnHover: true
    };

    return (
        <div className="slider-container">
            <div className="fade-left"></div>
            <Slider {...settings}>
                <div className="card"><h3>React</h3></div>
                <div className="card"><h3>JavaScript</h3></div>
                <div className="card"><h3>HTML</h3></div>
                <div className="card"><h3>CSS</h3></div>
                <div className="card"><h3>Git</h3></div>
                <div className="card"><h3>Node.js</h3></div>
            </Slider>
            <div className="fade-right"></div>
        </div>
    );
};

export default CardSlider;
