import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import About from './components/pt/About';
import Portfolio from './components/pt/Portfolio';
import Projects from './components/pt/Projects';
import Contact from './components/pt/Contacts';
import English from './components/en/En';
import Home from './components/pt/Home';
import usa from './components/pt/assets/usa.png';
import br from './components/pt/assets/brazil.png';
import Fade from 'react-reveal/Fade';

function Navigation({ toggleMenu, toggleTheme, isDarkMode, menuOpen, toggleLanguage, isEnglish }) {
  const [emojiTransition, setEmojiTransition] = useState(false);

  const handleToggleTheme = () => {
    setEmojiTransition(true);
    toggleTheme();
    setTimeout(() => {
      setEmojiTransition(false);
    }, 500);
  };

  const getLocalizedPath = (path) => {
    return isEnglish ? `/en${path}` : `/pt${path}`;
  };

  return (
    <nav className="navbar">
      <a className="logo" href={getLocalizedPath('/')}>Lass DEV</a>
      <button className={`menu-toggle ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
        <span></span><span></span><span></span>
      </button>
      <ul className={`menu ${menuOpen ? 'active' : ''}`}>
        <li><a href={getLocalizedPath('/')} className="active">Home</a></li>
        <li><a href={getLocalizedPath('/about')} className="active">Sobre</a></li>
        <li><a href={getLocalizedPath('/portfolio')} className="active">Portfolio</a></li>
        <li><a href={getLocalizedPath('/projects')} className="active">Projetos</a></li>
        <li><a href={getLocalizedPath('/contact')} className="active">Contato</a></li>
      </ul>
      <ul className="menu2">
        <li>
          <button onClick={toggleLanguage} className="active" style={{ cursor: 'pointer', background: 'none', border: 'none' }}>
            <img
              src={isEnglish ? usa : br}
              alt={isEnglish ? 'USA Flag' : 'Bandeira do Brasil'}
              style={{ width: '24px', height: '24px', marginLeft: '5px' }} 
            />
          </button>
        </li>
        <button className={`theme-toggle ${emojiTransition ? 'emoji-transition' : ''}`} onClick={handleToggleTheme}>
          {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </ul>
    </nav>
  );
}

function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [isEnglish, setIsEnglish] = useState(() => localStorage.getItem('language') === 'en');

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const toggleLanguage = () => {
    const newLanguage = isEnglish ? 'pt' : 'en';
    setIsEnglish(newLanguage === 'en');
    localStorage.setItem('language', newLanguage);
    window.location.href = `/${newLanguage}/`;
  };

  useEffect(() => {
    document.body.classList.toggle('dark', isDarkMode);
    document.body.classList.toggle('light', !isDarkMode);
  }, [isDarkMode]);

  return (
    <Router>
      <div className="App">
        <Fade>
          <header className="site-header">
            <Navigation 
              toggleMenu={toggleMenu} 
              toggleTheme={toggleTheme} 
              isDarkMode={isDarkMode} 
              menuOpen={menuOpen} 
              toggleLanguage={toggleLanguage}
              isEnglish={isEnglish}
            />
          </header>
        </Fade>

        <Routes>
          <Route path="/" element={<Navigate to={isEnglish ? "/en/" : "/pt/"} />} />
          <Route path="/pt/" element={<Home />} />
          <Route path="/pt/about" element={<About />} />
          <Route path="/pt/portfolio" element={<Portfolio />} />
          <Route path="/pt/projects" element={<Projects />} />
          <Route path="/pt/contact" element={<Contact />} />
          <Route path="/en/" element={<English />} />
        </Routes>

        <footer>
          <p>¬© Gustavo Lass [2024]</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
