import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const cardWidth = 200; // Largura de cada card
    const totalCards = cards.length * 2; // Duplicamos os cards para efeito contínuo
    const [offset, setOffset] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setOffset((prevOffset) => {
                const nextOffset = prevOffset + cardWidth; // Mova para a esquerda
                // Reinicie o offset se passar do número total de cards visíveis
                return nextOffset >= cardWidth * cards.length ? 0 : nextOffset;
            });
        }, 2000); // Tempo do intervalo de transição

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [cards.length, cardWidth]);

    return (
        <div className="slider-container">
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out", // Suaviza a transição
                }}
            >
                {/* Renderiza os cards */}
                {cards.map((card, index) => (
                    <div className="slide" key={index}>
                        <h3>{card}</h3>
                    </div>
                ))}
                {/* Duplicando os cards para o efeito contínuo */}
                {cards.map((card, index) => (
                    <div className="slide" key={`dup-${index}`}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
