import React from "react";
import Slider from "react-slick";
import "./CardSlider.css"; // Crie este arquivo para o CSS

// Ícones ou nomes das tecnologias para os cards
const techStack = [
    "React",
    "JavaScript",
    "HTML",
    "CSS",
    "Git",
    "Node.js"
];


const CardSlider = () => {
    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 3, // Número de slides visíveis
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        arrows: false,
        pauseOnHover: false
    };    
    

    return (
        <div className="slider-container">
            <div className="fade-left"></div>
            <Slider {...settings}>
                <div className="card">
                    
                </div>
            </Slider>

            <div className="fade-right"></div>
        </div>
    );
};

export default CardSlider;
