import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const cardWidth = 200; // Largura de cada card
    const [offset, setOffset] = useState(0);
    
    // Usamos a largura total para determinar o comportamento do slider
    const totalWidth = cardWidth * cards.length;

    useEffect(() => {
        const interval = setInterval(() => {
            setOffset((prevOffset) => {
                // Mova 200px para a esquerda
                const nextOffset = prevOffset + cardWidth;
                // Se o offset ultrapassar a largura total, reinicie
                return nextOffset >= totalWidth ? 0 : nextOffset;
            });
        }, 2000); // Tempo do intervalo de transição

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [totalWidth, cardWidth]);

    return (
        <div className="slider-container">
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out", // Suaviza a transição
                }}
            >
                {cards.map((card, index) => (
                    <div className="slide" key={index}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
            {/* Adicionando os mesmos cards para o efeito de repetição */}
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    position: "absolute",
                    left: "100%", // Posiciona a duplicata à direita
                    transition: "transform 0.5s ease-in-out",
                }}
            >
                {cards.map((card, index) => (
                    <div className="slide" key={`dup-${index}`}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
