import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const [offset, setOffset] = useState(0);
    const [isPaused, setIsPaused] = useState(false); // Estado para controlar a pausa
    const cardWidth = 140; // Largura de cada card
    const totalCards = cards.length; // Número total de cards
    const totalWidth = cardWidth * totalCards; // Largura total dos cards

    // Duplicar os cards para efeito de loop infinito
    const duplicatedCards = [...cards, ...cards];

    useEffect(() => {
        let interval;

        if (!isPaused) {
            interval = setInterval(() => {
                setOffset((prevOffset) => {
                    const nextOffset = prevOffset + cardWidth; // Mova 140px para a esquerda
                    // Se o próximo offset ultrapassa a largura total dos cards, redefine para 0
                    return nextOffset >= totalWidth ? 0 : nextOffset;
                });
            }, 2000); // Tempo do intervalo de transição
        }

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [isPaused, totalCards, cardWidth, totalWidth]);

    return (
        <div 
            className="slider-container"
            onMouseEnter={() => setIsPaused(true)} // Pausa ao passar o mouse
            onMouseLeave={() => setIsPaused(false)} // Retoma ao sair o mouse
        >
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out",
                    width: `${cardWidth * duplicatedCards.length}px`, // Largura total dos cards duplicados
                }}
            >
                {/* Renderizando os cards duplicados */}
                {duplicatedCards.map((card, index) => (
                    <div className="slide" key={index}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
