import React, { useEffect, useState } from "react";
import "./CardSlider2.css";

import psIcon from './assets/reactjs.png';
import vegasIcon from './assets/javascript.png';
import premiereIcon from './assets/html.png';
import blenderIcon from './assets/css.svg';
import c4dIcon from './assets/git2.png';
import unityIcon from './assets/node.png';
import ue4Icon from './assets/c++.png';
import csicon from './assets/cs.png';
import javaicon from './assets/java.png';
import openglicon from './assets/opengl.png';
import opencvicon from './assets/opencv.png';
import mongoicon from './assets/mongodb.png';
import neticon from './assets/net.svg';

const CardSlider2 = () => {
    const cards = [
        { name: "Photoshop", icon: reactIcon },
        { name: "Sony Vegas", icon: javascriptIcon },
        { name: "Adobe Premiere", icon: htmlIcon },
        { name: "Blender", icon: cssIcon },
        { name: "C4D", icon: gitIcon },
        { name: "Unity", icon: nodejsIcon },
        { name: "UE4", icon: cppicon },
        { name: "Arduino", icon: csicon },
        { name: "Linux", icon: javaicon },
    ];

    // O restante do código permanece igual
    const [offset, setOffset] = useState(0);
    const [isPaused, setIsPaused] = useState(false);
    const cardWidth = 130; 
    const totalCards = cards.length; 
    const totalWidth = cardWidth * totalCards; 

    const duplicatedCards = [...cards, ...cards];

    useEffect(() => {
        let interval;

        if (!isPaused) {
            interval = setInterval(() => {
                setOffset((prevOffset) => {
                    const nextOffset = prevOffset + cardWidth; 
                    return nextOffset >= totalWidth ? 0 : nextOffset;
                });
            }, 10000); 
        }

        return () => clearInterval(interval); 
    }, [isPaused, totalWidth, cardWidth]);

    return (
        <div 
            className="slider-container"
            onMouseEnter={() => setIsPaused(true)} 
            onMouseLeave={() => setIsPaused(false)} 
        >
            <div className="fade-right"></div>
            <div
                className="slider2"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out",
                    width: `${cardWidth * duplicatedCards.length}px`,
                }}
            >
                {duplicatedCards.map((card, index) => (
                    <div 
                        className="slide" 
                        key={index}
                        onMouseEnter={() => setIsPaused(true)} 
                        onMouseLeave={() => setIsPaused(false)} 
                    >
                        <img src={card.icon} alt={card.name} className="white-icon" />
                        <h3>{card.name}</h3>
                    </div>
                 ))}

            </div>
            <div className="fade-left"></div>
        </div>
    );
};

export default CardSlider2;
