import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import About from './components/pt/About';
import Portfolio from './components/pt/Portfolio';
import Projects from './components/pt/Projects';
import Contact from './components/pt/Contacts';
import English from './components/en/En.js';
import Home from './components/pt/Home.js';
import usa from './components/pt/assets/usa.png';
import br from './components/pt/assets/brazil.png';
import Fade from 'react-reveal/Fade';

function Navigation({ toggleMenu, toggleTheme, isDarkMode, menuOpen }) {
  const location = useLocation();
  const isEnglish = location.pathname.startsWith('/en');
  const [emojiTransition, setEmojiTransition] = useState(false);

  const handleToggleTheme = () => {
    setEmojiTransition(true);
    toggleTheme();
    setTimeout(() => {
      setEmojiTransition(false);
    }, 500); // Tempo igual ao da transição CSS
  };

  const handleLanguageChange = () => {
    const newLangPath = isEnglish ? "/pt/" : "/en/";
    localStorage.setItem('language', newLangPath); // Salva o idioma no localStorage
    window.location.href = newLangPath; // Redireciona para o idioma escolhido
  };

  return (
    <nav className="navbar">
      <a className="logo" href="/pt/">Lass DEV</a>
      <button className={`menu-toggle ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
        <span></span><span></span><span></span>
      </button>
      <ul className={`menu ${menuOpen ? 'active' : ''}`}>
        <li><a href="/pt/" className="active">Home</a></li>
        <li><a href="/pt/about" className="active">Sobre</a></li>
        <li><a href="/pt/portfolio" className="active">Portfolio</a></li>
        <li><a href="/pt/projects" className="active">Projetos</a></li>
        <li><a href="/pt/contact" className="active">Contato</a></li>
      </ul>
      <ul className="menu2">
        <li>
          <a onClick={handleLanguageChange} className="active">
            <img
              src={isEnglish ? usa : br}
              alt={isEnglish ? 'USA Flag' : 'Bandeira do Brasil'}
              style={{ width: '24px', height: '24px', marginLeft: '5px' }}
            />
          </a>
        </li>
        <button className={`theme-toggle ${emojiTransition ? 'emoji-transition' : ''}`} onClick={handleToggleTheme}>
          {isDarkMode ? '☀️' : '🌙'}
        </button>
      </ul>
    </nav>
  );
}

function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  useEffect(() => {
    document.body.classList.toggle('dark', isDarkMode);
    document.body.classList.toggle('light', !isDarkMode);
  }, [isDarkMode]);

  // Redireciona para o idioma armazenado no localStorage ao carregar
  useEffect(() => {
    const storedLang = localStorage.getItem('language');
    if (storedLang && window.location.pathname !== storedLang) {
      window.location.href = storedLang;
    }
  }, []);

  return (
    <Router>
      <div className="App">
        <Fade>
          <header className="site-header">
            <Navigation
              toggleMenu={toggleMenu}
              toggleTheme={toggleTheme}
              isDarkMode={isDarkMode}
              menuOpen={menuOpen}
            />
          </header>
        </Fade>

        <Routes>
          <Route path="/" element={<Navigate to="/pt/" />} />
          <Route path="/pt/" element={<Home />} />
          <Route path="/pt/about" element={<About />} />
          <Route path="/pt/portfolio" element={<Portfolio />} />
          <Route path="/pt/projects" element={<Projects />} />
          <Route path="/pt/contact" element={<Contact />} />
          <Route path="/en/" element={<English />} />
        </Routes>
        <footer>
          <p>© Gustavo Lass [2024]</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
