import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const cardWidth = 200; // Largura de cada card
    const [offset, setOffset] = useState(0);
    
    // Duplicar os cards para permitir um loop contínuo
    const duplicatedCards = [...cards, ...cards];

    useEffect(() => {
        const interval = setInterval(() => {
            setOffset((prevOffset) => {
                const nextOffset = prevOffset + cardWidth; // Mova 200px para a esquerda
                return nextOffset >= cardWidth * cards.length ? 0 : nextOffset; // Reinicie o offset se passar do total de cards
            });
        }, 2000); // Tempo do intervalo de transição

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [cards.length]);

    return (
        <div className="slider-container">
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out", // Suaviza a transição
                }}
            >
                {duplicatedCards.map((card, index) => (
                    <div className="slide" key={index}>
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
