import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import About from './components/pt/About';
import Portfolio from './components/pt/Portfolio';
import Projects from './components/pt/Projects';
import Contact from './components/pt/Contacts';
import English from './components/en/En.js';
import Home from './components/pt/Home.js';
import usa from './components/pt/assets/usa.png';
import br from './components/pt/assets/brazil.png';
import Fade from 'react-reveal/Fade';

function Navigation({ toggleMenu, toggleTheme, isDarkMode, menuOpen, currentLang, toggleLang }) {
  const isEnglish = currentLang === 'en';

  return (
    <nav className="navbar">
      <a className="logo" href={`/${currentLang}/`}>Lass DEV</a>
      <button className={`menu-toggle ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
        <span></span><span></span><span></span>
      </button>
      <ul className={`menu ${menuOpen ? 'active' : ''}`}>
        <li><a href={`/${currentLang}/`} className="active">Home</a></li>
        <li><a href={`/${currentLang}/about`} className="active">Sobre</a></li>
        <li><a href={`/${currentLang}/portfolio`} className="active">Portfolio</a></li>
        <li><a href={`/${currentLang}/projects`} className="active">Projetos</a></li>
        <li><a href={`/${currentLang}/contact`} className="active">Contato</a></li>
      </ul>
      <ul className="menu2">
        <li>
          <button onClick={toggleLang} className="lang-toggle">
            <img
              src={isEnglish ? usa : br}
              alt={isEnglish ? 'USA Flag' : 'Bandeira do Brasil'}
              style={{ width: '24px', height: '24px', cursor: 'pointer' }}
            />
          </button>
        </li>
        <button className={`theme-toggle ${isDarkMode ? 'dark' : 'light'}`} onClick={toggleTheme}>
          {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </ul>
    </nav>
  );
}

function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [currentLang, setCurrentLang] = useState('pt');

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const toggleLang = () => {
    setCurrentLang((prevLang) => (prevLang === 'pt' ? 'en' : 'pt'));
  };

  useEffect(() => {
    document.body.classList.toggle('dark', isDarkMode);
    document.body.classList.toggle('light', !isDarkMode);
  }, [isDarkMode]);

  return (
    <Router>
      <div className="App">
        <Fade>
          <header className="site-header">
            <Navigation 
              toggleMenu={toggleMenu} 
              toggleTheme={toggleTheme} 
              isDarkMode={isDarkMode} 
              menuOpen={menuOpen} 
              currentLang={currentLang}
              toggleLang={toggleLang}
            />
          </header>
        </Fade>

        <Routes>
          <Route path="/" element={<Navigate to={`/${currentLang}/`} />} />
          <Route path="/pt/" element={<Home />} />
          <Route path="/pt/about" element={<About />} />
          <Route path="/pt/portfolio" element={<Portfolio />} />
          <Route path="/pt/projects" element={<Projects />} />
          <Route path="/pt/contact" element={<Contact />} />
          <Route path="/en/" element={<English />} />
          {/* Adicione rotas para as demais p√°ginas em ingl√™s conforme necess√°rio */}
        </Routes>

        <footer>
          <p>¬© Gustavo Lass [2024]</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
