import React from "react";
import Slider from "react-slick";
import "./CardSlider.css"; // Crie este arquivo para o CSS

// Ícones ou nomes das tecnologias para os cards
const techStack = [
    "Hugo", "React", "Next.js", "Vite", "HTML", "CSS", "JavaScript",
    "Rust", "Git", "Java", "Spring", "Express", "PostgreSQL", "MariaDB"
];

const CardSlider = () => {
    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        arrows: false,
        pauseOnHover: false // Desativa a pausa ao passar o mouse
    };
    

    return (
        <div className="slider-container">
            <div className="fade-left"></div>
            <Slider {...settings}>
                {techStack.map((tech, index) => (
                    <div key={index} className="card">
                        {tech}
                    </div>
                ))}
            </Slider>
            <div className="fade-right"></div>
        </div>
    );
};

export default CardSlider;
