import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % cards.length);
        }, 3000); // Muda a cada 3 segundos

        return () => clearInterval(interval); // Limpa o intervalo ao desmontar o componente
    }, [cards.length]);

    const sliderStyle = {
        transform: `translateX(-${(currentIndex % 3) * 50}%)`, // Ajustando o deslocamento
        transition: "transform 0.5s ease", // Suavizando a transição
        display: 'flex',
        flexWrap: 'wrap',
        width: '200%', // Mantém o slider com largura suficiente para dois rows
    };

    return (
        <div className="slider-container">
            <div className="slider" style={sliderStyle}>
                {cards.map((card, index) => (
                    <div
                        key={index}
                        className={`slide ${index === currentIndex ? "active" : ""}`}
                    >
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CardSlider;
