import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
    const cards = ["React", "JavaScript", "HTML", "CSS", "Git", "Node.js"];
    const [offset, setOffset] = useState(0);
    const [isPaused, setIsPaused] = useState(false);
    const cardWidth = 140; 
    const totalCards = cards.length; 
    const totalWidth = cardWidth * totalCards; 

    const duplicatedCards = [...cards, ...cards];

    useEffect(() => {
        let interval;

        if (!isPaused) {
            interval = setInterval(() => {
                setOffset((prevOffset) => {
                    const nextOffset = prevOffset + cardWidth; 
                    return nextOffset >= totalWidth ? 0 : nextOffset;
                });
            }, 2000); 
        }

        return () => clearInterval(interval); 
    }, [isPaused, totalCards, cardWidth, totalWidth]);

    return (
        <div 
            className="slider-container"
            onMouseEnter={() => setIsPaused(true)} 
            onMouseLeave={() => setIsPaused(false)} 
        >
            <div className="fade-right"></div>
            <div
                className="slider"
                style={{
                    transform: `translateX(-${offset}px)`,
                    transition: "transform 0.5s ease-in-out",
                    width: `${cardWidth * duplicatedCards.length}px`,
                }}
            >
                {duplicatedCards.map((card, index) => (
                    <div 
                        className="slide" 
                        key={index}
                        onMouseEnter={() => setIsPaused(true)} // Pausa ao passar o mouse sobre o card
                        onMouseLeave={() => setIsPaused(false)} // Retoma ao sair o mouse do card
                    >
                        <h3>{card}</h3>
                    </div>
                ))}
            </div>
            <div className="fade-left"></div>
        </div>
    );
};

export default CardSlider;
