import './Home.css';
import will from './assets/will.jpg';
import cf from './assets/cf.png';
import discord from './assets/discord.png';
import git from './assets/git.png';
import instagram from './assets/instagram.png';
import telegram from './assets/telegram.png';
import horn from './assets/mlg.mp3';
import { useState, useRef } from 'react';
import Zoom from 'react-reveal/Zoom'; 
import Fade from 'react-reveal/Fade';
import Bounce from 'react-reveal/Bounce';
import CardSlider from "./CardSlider/CardSlider";
import CardSlider2 from "./CardSlider/CardSlider2";

function Home() {
    const [crosshairs, setCrosshairs] = useState([]);
    const isPlaying = useRef(false); 

    const handleImageClick = () => {
      if (isPlaying.current) return; 

      const newCrosshairs = Array.from({ length: 10 }, () => {
        const size = 50; 
        const x = Math.random() * (window.innerWidth - size); 
        const y = Math.random() * (window.innerHeight - size); 
        return {
          top: `${y}px`,
          left: `${x}px`,
          opacity: 1,
        };
      });
      setCrosshairs([]);

      const audio = new Audio(horn);
      audio.play();
      isPlaying.current = true; 

      setTimeout(() => {
        setCrosshairs([]);
      }, 1000);
  
      setTimeout(() => {
        isPlaying.current = false; 
      }, 7500);
    };

    const handleDragStart = (e) => {
        e.preventDefault(); 
    };

    // Dados da experiência
    const experiences = [
        { title: "Desenvolvedor Solo", company: "Discord", date: "Jan 2016 - 2018", description: "Desenvolvimento de bots para servidores." },
        { title: "Desenvolvedor Front-End", company: "Freelancer", date: "Jul 2017 - Presente", description: "Desenvolvimento de front-ends para websites." },
        { title: "Desenvolvedor de Cheats", company: "Vários Clientes", date: "Jan 2020 - Jan 2024", description: "Desenvolvimento avançado de cheats." },
        { title: "Estudante de Ciência da Computação", company: "UDESC", date: "Jul 2024 - Presente", description: "Cursando Ciência da Computação." },
    ];

    // Dados da nova timeline
    const timelineEvents = [
        { title: "Início da Jornada", date: "2016", description: "Comecei a explorar a programação e desenvolvi meu primeiro projeto." },
        { title: "Criação do Meu Primeiro Bot", date: "2017", description: "Desenvolvi um bot para o Discord que facilitava a interação em grupos." },
        { title: "Freelancer", date: "2018", description: "Iniciei meu trabalho como freelancer, ajudando empresas a melhorar sua presença online." },
        { title: "Desenvolvimento de Cheats", date: "2020", description: "Aprofundei meus conhecimentos em programação de cheats e sistemas de bypass." },
        { title: "Estudante de Ciência da Computação", date: "2024", description: "Entrei na UDESC para aprimorar meus conhecimentos técnicos." },
    ];

    return (
        <main className="content">
            <div className="title-container">
                <div className="title">
                    <Fade delay={201}>
                        <label className="name">Gustavo "Dark" Lass</label>
                        <div className="divider"></div>
                        <label className="undertext">Full-Stack Developer - Bacharelado em Ciência da Computação pela UDESC. <br /> 17 Anos, 7 anos+ de experiência em desenvolvimento</label>
                    </Fade>
                    <Fade delay={501}>
                        <ul className="buttons"> 
                            <li><a target="_blank" href="https://github.com/D4rkks"><img title="Github" className="img" src={git} /></a></li>
                            <li><a target="_blank" href="https://codeforces.com/profile/D4rkks"><img title="Codeforces" className="img" src={cf} /></a></li>
                            <li><a target="_blank" href="https://discord.com/users/247726496435666944/"><img title="Discord" className="img" src={discord} /></a></li>
                            <li><a target="_blank" href="https://www.instagram.com/gustavo.lass/"><img title="Instagram" className="img" src={instagram} /></a></li>
                            <li><a href=""><img title="Telegram" className="img" src={telegram} /></a></li>
                        </ul>
                    </Fade>
                </div>
                <Bounce>
                    <div className="will-smith" onClick={handleImageClick}>
                        <img 
                            src={will} 
                            alt="Im Jobless" 
                            className="will-smith" 
                            onDragStart={handleDragStart} 
                        />
                    </div>
                </Bounce>
            </div>

            {crosshairs.map((style, index) => (
                <div key={index} className="crosshair" style={style}></div>
            ))}

            <Fade delay={1001}>
                <div className="skills">
                    <label className="title">Skills</label>
                    <CardSlider />
                    <CardSlider2 />
                </div>
            </Fade>

            <div className="experience">
                <Fade delay={2001}>
                    <label className="title">Experiência</label>
                </Fade>
                <Fade delay={2501}>
                    <div className="timeline">
                        {experiences.map((exp, index) => (
                            <div className="timeline-item" key={index}>
                                <h3>{exp.title} - <span>{exp.company}</span></h3>
                                <p className="date">{exp.date}</p>
                                <p>{exp.description}</p>
                            </div>
                        ))}
                    </div>
                </Fade>
            </div>

            <div className="historia">
                <Fade delay={3001}>
                    <label className="title">Minha História</label>
                </Fade>
                <Fade delay={3501}>
                    <div className="timeline">
                        {timelineEvents.map((event, index) => (
                            <div className="timeline-item" key={index}>
                                <h3>{event.title}</h3>
                                <p className="date">{event.date}</p>
                                <p>{event.description}</p>
                            </div>
                        ))}
                    </div>
                </Fade>
            </div>
        </main>
    );
}

export default Home;
